import React, { useContext, useEffect, useState } from "react";
import './blog.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash, faPen, faEye } from '@fortawesome/free-solid-svg-icons'
import axios from "axios";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";
import mycontext from "../context/mycontext";


function Blog() {
    
    const {changedata,setchangedata} = useContext(mycontext)
   


    const location = useLocation()
    const Navigate = useNavigate()
    const [toggle, settoggle] = useState(true)
    const [data, setdata] = useState([]);


    const handledelete = (items) => {
        console.log(items);
        axios.delete("http://localhost:4200/data/" + items.id).then((res) => {
            console.log(res);
            getdata()
        }).catch((err) => {
            console.log(err);
        });
    }

    const d = new Date();
    const date = d.getDate();
    const month = d.getMonth();
    const year = d.getFullYear();
    const fulldate = `${date}-${month}-${year}`;



    const handleview = (i) => {
        const { id } = i;

        Navigate('/viewblog', { state: { view: id } })
    }

    const editpost = (item)=>{
        setchangedata(false)
        
        if(changedata == false){
            Navigate("/addblog",{state:{check:item}})
        }
    }

    const getdata = () => {
        axios.get("http://localhost:4200/data").then((res) => {
            setdata(res.data)
        }).catch((err) => {
            console.log(err);
        })
    }



    useEffect(() => {
        getdata()
    }, [])
    return (
        <>
        <div className="flex items-center justify-center min-h-screen bg-gray-900">
            <div className="col-span-12">
                <div className=" lg:overflow-hidden sm:text-9xl ">
                    <table className="table text-gray-400 border-separate space-y-6 text-sm">
                        <thead className="bg-gray-800 text-gray-500">
                            <tr>
                                <th className="p-3">Post No.</th>
                                <th className="p-3 text-left">Title</th>
                                <th className="p-3 text-left">Subtitle</th>
                                <th className="p-3 text-left">Date</th>
                                <th className="p-3 text-left">Status</th>
                                <th className="p-3 text-left">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                data.map((item, index) => {
                                    return (
                                        <tr className="bg-gray-800">
                                            <td className="p-3">
                                                {index + 1}
                                            </td>
                                            <td className="p-3">
                                                {item.title}
                                            </td>
                                            <td className="p-3 font-bold">
                                                {item.subtitle}
                                            </td>
                                            <td className="p-3 font-bold">
                                                {fulldate}
                                            </td>
                                            <td className="p-3 font-bold">
                                                <span className="bg-green-400 text-gray-50 rounded-md p-1">Posted</span>
                                            </td>
                                            <td className="p-3 ">
                                                <button className="text-gray-400 hover:text-gray-100 mr-2">
                                                    <FontAwesomeIcon icon={faEye} onClick={() => handleview(item)} />
                                                </button>
                                                <button href="#" className="text-gray-400 hover:text-gray-100  mx-2">
                                                    <FontAwesomeIcon icon={faPen} onClick={() =>editpost(item) } />
                                                </button>
                                                <button href="#" className="text-gray-400 hover:text-gray-100  ml-2">
                                                    <FontAwesomeIcon icon={faTrash} onClick={() => handledelete(item)} />
                                                </button>
                                            </td>
                                        </tr>
                                    )
                                })
                            }

                        </tbody>
                    </table>
                </div>
            </div>
        </div>
        
        </>
    );
}

export default Blog;
