import React, { useEffect, useState } from "react";
import './Viewblog.css'
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLeftLong } from '@fortawesome/free-solid-svg-icons'

function Viewblog(){

    const Navigate = useNavigate()
    const location = useLocation()
    const [data,setdata]=useState([]);

    console.log(location.state);

    const filterblog = data.filter((item)=>item.id === location.state.id || item.id === location.state.view &&  item);

    useEffect(()=>{
        axios.get("http://localhost:4200/data").then((res)=>{
            setdata(res.data)
        }).catch((err)=>{
            console.log(err);
        })
    },[])
    return(
        <div className="container m-auto  border-gray-400 py-12 flex flex-col justify-center items-center p-2">
            <div className="absolute top-5 text-3xl" onClick={()=>Navigate("/home")}><FontAwesomeIcon icon={faLeftLong}></FontAwesomeIcon></div>
            {
                filterblog.map((item)=>{
                    return(
                        <>
                        <div class="font-bold text-5xl mb-2 pb-5 text-center py-12 font-mono md:text-xl">{item.title}</div>
            <img class="w-full" src={item.img} alt="Sunset in the mountains" />

            <p class="text-blue-900  py-10  text-2xl slab text-center">{item.subtitle} </p>

            <p class="  text-2xl karla">{item.about}</p>
                        </>
                    );
                })
            }
        </div>
    );
}

export default Viewblog;
