import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";
import { toast } from 'react-toastify';
import mycontext from "../context/mycontext";



function Addblog() {

    const location = useLocation();
    const Navigate = useNavigate();

    const {changedata,setchangedata} = useContext(mycontext);
    const [toggle, settoggle] = useState(changedata);
    const [blogdata, setblogdata] = useState([]);
    const [data, setdata] = useState({
        title: "",
        subtitle: '',
        about: null,
        img: null
    })



    const [change, setchange] = useState({
        title: "",
        subtitle: '',
        about: null,
        img: null
    })

    const getdata = () => {
        axios.get("http://localhost:4200/data").then((res) => {
            setblogdata(res.data)
        }).catch((err) => {
            console.log(err);
        });
    }


    const handlepost = () => {
        if (data.title === null) {
            toast("Please Enter Title")
        }
        else if (data.subtitle === null) {
            toast("Please Enter Subtitle")
        }
        else if (data.subtitle.length <= 50) {
            toast("Please Enter Min. 50 Word")
        }
        else if (data.about === null) {
            toast("Please Enter About")
        } else if (data.img === null) {
            toast("Please Upload file")
        }
        else if (data.title != null && data.subtitle != null && data.about != null && data.img != null) {
            axios.post("http://localhost:4200/data", data).then((res) => {
                Navigate("/home")
                toast.success("Posted Successful")
            }).catch((err) => {
                console.log(err);
            });
        }

    }

    const handleedit = () => {
        const item = location.state.check
        console.log();
        axios.patch("http://localhost:4200/data/" + location.state, {
            title: change.title  ? change.title :item.title, 
            subtitle: change.subtitle ? change.subtitle : item.subtitle,
            about: change.about ? change.about : item.about,
            img: change.img ? change.img : item.img
        }).then((res) => {
            setchangedata(true)
            // Navigate('/blog')
        }).catch((err) => {
            console.log(err);
        })
    };

    useEffect(() => {
        getdata();
    }, []);


    return (

        <div className="flex min-h-full flex-col justify-center px-6 py-10 lg:px-8 ">
            <div className="sm:mx-auto sm:w-full sm:max-w-sm">

                <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">{toggle ? "Add New post" : "Edit Post"}</h2>
            </div>

            <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm ">
                <form className="space-y-6" action="#" method="POST">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium leading-6 text-gray-900">Blog Title</label>
                        <div className="mt-2">
                            <input id="title" name="title" type="text" required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6" onChange={(e) => toggle ? setdata((prev) => ({ ...prev, title: e.target.value.toUpperCase() })) : setchange((prev) => ({ ...prev, title: e.target.value.toUpperCase() }))} />
                        </div>
                    </div>

                    <div>
                        <div className="flex items-center justify-between">
                            <label htmlFor="password" className="block text-sm font-medium leading-6 text-gray-900">Sub Title</label>

                        </div>
                        <div className="mt-2">
                            <input id="subtitle" name="subtitle" type="text" autocomplete="current-password" required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6" onChange={(e) => toggle ? setdata((prev) => ({ ...prev, subtitle: e.target.value })) : setchange((prev) => ({ ...prev, subtitle: e.target.value }))} />
                            <div className="flex justify-between">
                                <p className="mt-3 text-sm leading-6 text-gray-600 text-right">Write 50 Words in Subtitle. </p>
                                <p className="mt-3 text-sm leading-6 text-gray-600 text-right">{(data.subtitle).length + '/500'}</p>

                            </div>
                        </div>
                    </div>

                    <div className="col-span-full">
                        <label htmlFor="about" className="block text-sm font-medium leading-6 text-gray-900">About Post</label>
                        <div className="mt-2">
                            <textarea id="about" name="about" rows="3" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6" onChange={(e) => toggle ? setdata((prev) => ({ ...prev, about: e.target.value })) : setchange((prev) => ({ ...prev, about: e.target.value }))}></textarea>
                        </div>
                        <p className="mt-3 text-sm leading-6 text-gray-600">Write a few sentences about your post.</p>
                    </div>

                    <div className="col-span-full">
                        <label htmlFor="cover-photo" className="block text-sm font-medium leading-6 text-gray-900">Cover photo</label>
                        <div className="mt-2 flex justify-center rounded-lg border border-dashed border-gray-900/25 px-6 py-10">
                            <div className="text-center">
                                <svg className="mx-auto h-12 w-12 text-gray-300" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                                    <path fill-rule="evenodd" clip-rule="evenodd" />
                                </svg>
                                <div className="mt-4 flex text-sm leading-6 text-gray-600">
                                    <label htmlFor="file-upload" className="relative cursor-pointer rounded-md bg-white font-semibold text-indigo-600 focus-within:outline-none focus-within:ring-2 focus-within:ring-indigo-600 focus-within:ring-offset-2 hover:text-indigo-500">
                                        <span>Upload a file</span>
                                        {
                                            toggle ? <input id="file-upload" name="file-upload" type="file" className="sr-only" onChange={(e) => {
                                                const reader = new FileReader()
                                                reader.readAsDataURL(e.target.files[0])
                                                reader.onloadend = () => {
                                                    setdata((prev) => ({ ...prev, img: reader.result }))
                                                }
                                            }
                                            }
                                            /> :

                                                <input id="file-upload" name="file-upload" type="file" className="sr-only" onChange={(e) => {
                                                    const reader = new FileReader()
                                                    reader.readAsDataURL(e.target.files[0])
                                                    reader.onloadend = () => {
                                                        setchange((prev) => ({ ...prev, img: reader.result }))
                                                    }
                                                }
                                                }
                                                />
                                        }
                                    </label>

                                </div>
                                <p className="text-xs leading-5 text-gray-600">PNG, JPG, GIF up to 10MB</p>
                            </div>
                        </div>
                    </div>

                    <div>


                        <Link className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600" onClick={() => { toggle ? handlepost() : handleedit() }}>{toggle ? "Post Blog" : "Edit Post"}</Link>


                    </div>
                </form>


            </div>
        </div>

    );
}

export default Addblog;
