import { createSlice } from "@reduxjs/toolkit";


const CartSlice = createSlice({
    name : 'toggle',
    initialState: {
        toggle : false
    },
    reducers: {
        toggleHead (state, action) {
            state.toggle = action.payload;
        }
    }
    
})

export { CartSlice };
export const {toggleHead} = CartSlice.actions;
